InfoItem.value:
if (!Object.hasOwnProperty("triggered")) Object.triggered = false;
if (event.value > 52 && !Object.triggered) {
  Object.triggered = true;
  odf.writeItem(true, "Objects/Servo/Switch");
}
if (event.value < 45 && Object.triggered) {
  Object.triggered = false;
  odf.writeItem(false, "Objects/Servo/Switch");
}

var sleep = function (milliseconds) {
  const date = Date.now();
  let curDate = null;
  do {
    curtDate = Date.now();
  } while (curtDate - date < milliseconds);
};
odf.writeItem(0, "Objects/Servo/SetDegrees");
sleep(2000);
odf.writeItem(180, "Objects/Servo/SetDegrees");
